import './css/reset.css';
import './css/App.css';

import Detail from "./page/detail.js";
import Main from "./page/main.js";

import { useState } from 'react';
import data from "./js/data.js";
import { Routes, Route, Link, useNavigate, Outlet } from "react-router-dom";


function App() {
  let [shoes] = useState(data);
  let navigate = useNavigate(); // 페이지 이동을 도와주는 함수입니다.

  return (
    <div className="App">
      <Header navigate={navigate}/>
      <div>
      <Routes>
        <Route path="/" element={<Main shoes={shoes}/>} />
        <Route path="/detail" element={<Detail />} />
        {/* <Route path="/about" element={<><h1>ABOUT</h1><p onClick={() => {navigate("/about/sec1")}}>sec1 이동하기</p><p onClick={() => {navigate("/about/sec2")}}>sec2 이동하기</p></>}>
          <Route path="/sec1" element={<><h1>ABOUT 속 sec1</h1><p onClick={() => {navigate("/about/sec1")}}>sec1 이동하기</p><p onClick={() => {navigate("/about/sec2")}}>sec2 이동하기</p></>}></Route>
          <Route path="/sec2" element={<><h1>ABOUT 속 sec2</h1><p onClick={() => {navigate("/about/sec1")}}>sec1 이동하기</p><p onClick={() => {navigate("/about/sec2")}}>sec2 이동하기</p></>}></Route>
        </Route> */}
        <Route path="/about" element={<div><h1>ABOUT</h1></div>}></Route>
        <Route path="*" element={<h1>NOT FOUND입니다.</h1>} />
      </Routes>
      </div>
    </div>
  );
}



function Header(props) {
  return (
    <header>
      <ul className="header-wrap">
        <li><Link to="/"><p>메인페이지</p></Link></li>
        <li><p onClick={() => { props.navigate(-1) }}>뒤로가기</p></li>
        <li><p onClick={() => { props.navigate(+1) }}>앞으로가기</p></li>
        <li><Link to="/detail"><p>상품 상세페이지</p></Link></li>

        <li><Link to="/about"><p>ABOUT페이지</p></Link></li>
      </ul>
    </header>
  )
}

export default App;
