import './css/reset.css';
import './css/App.css';
import './css/main.css';

import Detail from "./page/detail.js";

import { useState } from 'react';
import data from "./js/data.js";
import { Routes, Route, Link } from "react-router-dom";


function App() {
  let [shoes] = useState(data);
  return (
    <div className="App">
      <Header />
      <Routes>
        <Route path="/" element={<Main/>
        } />
        <Route path="/detail" element={<Detail />} />
        <Route path="*" element={<h1>NOT FOUND입니다.</h1>} />
      </Routes>
    </div>
  );
}

function Main(props) {
  return (
    <>
      <h1 className="pageName">메인페이지</h1>
      <section className="product">
        <div className="product-wrap">
          {
            shoes.map((value, index) => {
              return <List key={index} value={value} />
            })
          }
        </div>
      </section>
    </>
  )
}

function List(props) {
  return (
    <div className="list">
      <h1 className={`title`}>{props.value.title}</h1>
      <p className="content">{props.value.content}</p>
      <p className="price">{props.value.price}₩</p>
    </div>
  )
}

function Header(props) {
  return (
    <header>
      <ul className="header-wrap">
        <li><Link to="/"><p>메인페이지</p></Link></li>
        <li><Link to="/detail"><p>상품 상세페이지</p></Link></li>
      </ul>
    </header>
  )
}

export default App;
